	end
end

basicnumber.script:
function onHover(oncontrol)
	if oncontrol then
		local draginfo = Input.getDragData();
		if draginfo and draginfo.isType("number") and draginfo.getNumberData() < getValue() then
			setColor("BB0000");
		end
	else
		setColor(nil);
	end
end

function onDrop(x, y, draginfo)
	if draginfo.isType("number") and draginfo.getNumberData() > getValue() then
		setValue(draginfo.getNumberData());
	end

	return true;
end

windowclass.script:
function action(draginfo)
	local nodeChar = getDatabaseNode().getChild("...")
	local rActor = ActorManager.getActor("pc", nodeChar);

	ActionRecovery.performRoll(draginfo, rActor, getDatabaseNode());
end

function onDragStart(button, x, y, draginfo)
	action(draginfo);
	return true;
end

function onDoubleClick(x, y)
	action();
	return true;
end

linkfield.script:
function onInit()
	onValueChanged();
end
function onValueChanged()
	setVisible(not isEmpty());
end

basicdice.script:
function onDragStart(button, x, y, draginfo)
	return window.onDragStart(button, x, y, draginfo);
end

function onDoubleClick(x, y)
	return window.onDoubleClick(x, y);
end

windowtitlebar.script:
function setValue(sTitle)
    if widgetTitle then
        if prefix then
            sTitle = Interface.getString(prefix[1]) .. " - " .. sTitle;
        end
        widgetTitle.setText(sTitle);
        updatePosition();
    end
end

function setValue(sTitle)
    if widgetTitle then
        if prefix then
            sTitle = Interface.getString(prefix[1]) .. " - " .. sTitle;
        end
        widgetTitle.setText(sTitle);
        updatePosition();
    end
end
