end

stringu.script:
function onDrop(x, y, draginfo)
  if draginfo.isType("shortcut") then
    local sClass = draginfo.getShortcutData();
    if sClass == "reference_tableresult" then
      local node = draginfo.getDatabaseNode();
      local sNew = DB.getValue(node, "result", "");
      if sNew ~= "" then
        local aMerge = { };
        local sOld = getValue();
        if sOld ~= "" then
          table.insert(aMerge, sOld);
        end
        table.insert(aMerge, sNew);
        setValue(table.concat(aMerge, ";\n"));
      end
      return true;
    end
  end
end

list_text.script:
function onInit()
  registerMenuItem(Interface.getString("list_menu_createitem"), "insert", 5);
end

function onMenuSelection(selection,subselection)
  if selection == 5 then
    self.addEntry(true);
  end
end

function onListChanged()
  update();
end

function update(bEdit)
  if bEdit == nil then 
    if (window.parentcontrol.window.weapons.subwindow and window.parentcontrol.window.weapons.subwindow.actions_iedit) then
      bEdit = (window.parentcontrol.window.weapons.subwindow.actions_iedit.getValue() == 1);
    elseif (window.parentcontrol.window.parentcontrol.window.actions_iedit) then
      bEdit = (window.parentcontrol.window.parentcontrol.window.actions_iedit.getValue() == 1);
    end
  end
  for _,w in ipairs(getWindows()) do
    w.idelete.setVisibility(bEdit);
  end
end

function addEntry(bFocus)
  return createWindow();
end

